drop table if exists AOC_2024_Day09_Spaces
create table AOC_2024_Day09_Spaces(ord int,
									Span int
									)
create unique clustered index IX_AOC_2024_Day09_Spaces on AOC_2024_Day09_Spaces(ord)
GO
create or alter function fn_AOC_2024_Day09_MoveFile(@Ord int,
													@Span int,
													@FileID int,
													@Movements varchar(max)
													) returns table
as
return with m as
			(select o, s, n
				from string_split(@Movements, ',')
					cross apply (select cast(parsename([value], 4) as int) o
										, cast(parsename([value], 2) as int) s
										, cast(parsename([value], 3) as int) n
								) p
			)
			, i as
				(select s.ord, Span, n.NewOrd
						from AOC_2024_Day09_Spaces s
								left hash join m on o = ord
								cross apply (select isnull(n + s, ord) NewOrd) n
					
							where s.ord < @Ord
								and s.Span >= @Span
				)
				, i1 as
				(select ord, Span, max(NewOrd) NewOrd
					from i
					group by ord, Span
				)
		select top 1 concat(ord , '.', NewOrd , '.', @Span , '.' , @FileID) NewMove
		from i1
			cross apply (select Span - (NewOrd - ord) Remaining) n
		where Remaining >= @Span
		order by ord
GO
declare @Input varchar(max) =
''

drop table if exists #Input
drop table if exists #Files
drop table if exists #Final

;with Input as
	(select [value] - 1 ord, iif([value] % 2 = 1, [value]/2, -1 *[value]) FileID, cast(substring(@Input, [value], 1) as int) Span
		from generate_series(cast(1 as int), cast(len(@Input) as int))
	)
select row_number() over(order by ord, [value]) - 1 ord, FileID
into #Input
from Input
	cross apply generate_series(cast(1 as int), Span, 1)
order by ord

;with Files as
	(select ord, FileID, row_number() over(order by ord desc) ID
		from #Input
		where FileID > -1
	)
	, Spaces as
	(select ord, row_number() over(order by ord) ID
		from #Input
		where FileID < 0
	)
select sum(isnull(s.ord, f.ord) * FileID) Answer1
from Files f
	left join Spaces s on s.ID = f.ID
						and s.ord < f.ord

--Answer2
select min(ord) ord, FileID, max(ord) - min(ord) + 1 Span, cast(row_number() over(order by FileID desc) as int) ID
into #Files
from #Input
where FileID > -1
group by FileID

create unique clustered index IX_#Files on #Files(ID)

insert into AOC_2024_Day09_Spaces
select min(ord) ord, max(ord) - min(ord) + 1 Span
from #Input
where FileID < 0
group by FileID

drop table if exists ##Day09b
create table ##Day09b(ID int, Movements varchar(max), StepID int)
;with rec as
	(select cast(0 as int) ID, cast(null as varchar(max)) Movements, 0 StepID
	union all
	select f.ID, cast(isnull(r.Movements, '') + iif(Movements is not null and m.NewMove is not null, ',', '') + isnull(m.NewMove, '') as varchar(max)) Movements, r.StepID + 1 StepID
	from rec r
		cross apply (select r.ID + 1 ID) n
		inner join #Files f on f.ID = n.ID
		outer apply fn_AOC_2024_Day09_MoveFile(f.ord, f.Span, f.FileID, r.Movements) m
	)
select top 1 *
into #Final
from rec
order by StepID desc
option (maxrecursion 32767)

;with m as
	(select cast(parsename([value], 3) as int) o
			, cast(cast(parsename([value], 1) as int) as int) f
		from #Final
			cross apply string_split(Movements, ',')
	)
select sum(cast(n.ord + [value] as bigint) * cast(FileID as bigint)) Answer2
from #Files
	left join m on f = FileID
	cross apply (select isnull(o, ord) ord) n
	cross apply generate_series(cast(0 as int), cast(Span - 1 as int), cast(1 as int))
order by 1